def setup_platform(hass, config, add_devices, discovery_info=None):
    """Set up the Demo climate devices."""
    add_devices([
        DemoClimate('Toshiba', 22, TEMP_CELSIUS, TOSHIBA, 'heat'),
        DemoClimate('Panasonic', 22, TEMP_CELSIUS, PANASONIC, 'heat')
    ])


class DemoClimate(ClimateDevice):
    """Representation of a demo climate device."""

    def __init__(self, name, target_temperature, unit_of_measurement, mode_data, default_on_mode):
        """Initialize the climate device."""
        self._name = name
        self._target_temperature = target_temperature
        self._unit_of_measurement = unit_of_measurement
        self._current_operation = default_on_mode
        self._last_on_mode = default_on_mode
        self._operation_list = ['heat', 'cool']
        self._on = False
        self._mode_data = mode_data

    @property
    def supported_features(self):
        """Return the list of supported features."""
        return SUPPORT_FLAGS

    @property
    def should_poll(self):
        """Return the polling state."""
        return False

    @property
    def name(self):
        """Return the name of the climate device."""
        return self._name

    @property
    def temperature_unit(self):
        """Return the unit of measurement."""
        return self._unit_of_measurement

    @property
    def target_temperature(self):
        """Return the temperature we try to reach."""
        return self._target_temperature

    @property
    def current_operation(self):
        """Return current operation ie. heat, cool, idle."""
        return self._current_operation

    @property
    def operation_list(self):
        """Return the list of available operation modes."""
        return self._operation_list

    @property
    def is_on(self):
        """Return true if the device is on."""
        return self._on

    def set_temperature(self, **kwargs):
        """Set new target temperatures."""
        if kwargs.get(ATTR_TEMPERATURE) is not None and isinstance(self._mode_data[self._current_operation], dict):
            target_temp = kwargs.get(ATTR_TEMPERATURE)

            min_temp_in_mode = min(self._mode_data[self._current_operation].keys())
            max_temp_in_mode = max(self._mode_data[self._current_operation].keys())

            target_temp = max(target_temp, min_temp_in_mode)
            target_temp = min(target_temp, max_temp_in_mode)

            self._target_temperature = target_temp
            self.schedule_update_ha_state()

    def set_operation_mode(self, operation_mode):
        """Set new target temperature."""
        self._current_operation = operation_mode
        if self._current_operation != 'off':
            self._last_on_mode = self._current_operation
        self.schedule_update_ha_state()

    def turn_on(self):
        """Turn on."""
        self._on = True
        self._current_operation = self._last_on_mode
        self.schedule_update_ha_state()

    def turn_off(self):
        """Turn off."""
        if self._current_operation != 'off':
            self._last_on_mode = self._current_operation
        self._on = False
        self._current_operation = 'off'
        self.schedule_update_ha_state()

    @property
    def target_temperature_step(self):
        return 2

    def schedule_update_ha_state(self, **kwargs):
        super(DemoClimate, self).schedule_update_ha_state(kwargs)

        target_command = self._mode_data[self._current_operation]
        if isinstance(target_command, dict):
            target_command = target_command.get(int(self._target_temperature), None)

        if not target_command:
            return

        # TODO - Check why logging does not work.
        # TODO - Debug why the upper most on/off button does not change the state of the AC.
        # TODO - Try to remove the upper two elements from the climate UI.
        _LOGGER.info("AC %s, setting to mode: %s, temp: %s"
                     % (self._mode_data['ip_address'], self._current_operation, self._target_temperature))

        url = 'http://%s/exec' % self._mode_data['ip_address']
        body = dumps({
            'command': target_command,
            'frequency': 38
        })

        response = post(
            url=url,
            data=body,
            headers={
                'Content-Type': 'application/json'
            }
        )

        # TODO - Check if set_mode can return success/failure status.
        _LOGGER.debug("Set mode for %s completed with status code %s"
                      % (self._mode_data['ip_address'], response.status_code))

    @property
    def min_temp(self):
        """Return the minimum temperature."""
        min_temp = min(list(self._mode_data['heat'].keys()) + list(self._mode_data['cool'].keys()))
        if isinstance(self._mode_data[self._current_operation], dict):
            min_temp = min(self._mode_data[self._current_operation].keys())

        return convert_temperature(min_temp, TEMP_CELSIUS, self.temperature_unit)

    @property
    def max_temp(self):
        """Return the maximum temperature."""
        max_temp = max(list(self._mode_data['heat'].keys()) + list(self._mode_data['cool'].keys()))
        if isinstance(self._mode_data[self._current_operation], dict):
            max_temp = max(self._mode_data[self._current_operation].keys())

        return convert_temperature(max_temp, TEMP_CELSIUS, self.temperature_unit)
